<script setup>
import $ from 'jquery'
import { ref, watch, onMounted } from 'vue';
import { useRouter, useRoute } from 'vue-router'
import { userStore } from "../store/modules/userStore"

const UserStore = userStore()
const route = useRoute()
const router = useRouter()

const pathName = ref(route.name)

onMounted(() => {
    let titlePage, task, customcare, employeemanager ,financialmanagement;
    task = 'Quản lý công việc';
    customcare = 'Chăm sóc khách hàng';
    employeemanager = 'Quản lý nhân viên';
    financialmanagement = 'Quản lý ngân sách khách hàng';
    titlePage = task;
    if (route.name == 'Task' || route.name == 'TaskDetail') {
        titlePage = task;
        document.title = task;
    } else if (route.name == 'CusTomCare') {
        titlePage = customcare;
        document.title = customcare;
    } else if (route.name == 'EmployeeManager' || route.name == 'EditEmployeeManager') {
        titlePage = employeemanager;
        document.title = employeemanager;
    } else if (route.name == 'FinancialManagement') {
        titlePage = financialmanagement;
        document.title = financialmanagement;
    } 
    $('#titlePage').text(titlePage);
});

watch(() => route.name, (newName, oldName) => {
    let titlePage, task, customcare, employeemanager ,financialmanagement;
    task = 'Quản lý công việc';
    customcare = 'Chăm sóc khách hàng';
    employeemanager = 'Quản lý nhân viên';
    financialmanagement = 'Quản lý ngân sách khách hàng';
    titlePage = task;
    if (route.name == 'Task' || route.name == 'TaskDetail') {
        titlePage = task;
        document.title = task;
    } else if (route.name == 'CusTomCare') {
        titlePage = customcare;
        document.title = customcare;
    } else if (route.name == 'EmployeeManager' || route.name == 'EditEmployeeManager') {
        titlePage = employeemanager;
        document.title = employeemanager;
    } else if (route.name == 'FinancialManagement') {
        titlePage = financialmanagement;
        document.title = financialmanagement;
    } 
    $('#titlePage').text(titlePage);
    pathName.value = newName
});

const routerPush = (name, params = {}) => {
  router.push({
    name,
    params
  })
}
</script>

